{"title":"co2vans_enedc-kw","type":"vega","aggs":[],"params":{"spec":"{
  "$schema": "https://vega.github.io/schema/vega-lite/v2.json",
  "title": "",
  "data": {
    "url": {
      "%context%": true,

      "index": "co2_vans_dashboard_prod",
      "body": {
        "aggs": {
          "full_ewltp": {
            "terms": {"field": "MS", "size": 100},
            "aggs": {
              "ewltp": {
                "avg": {"field": "Ewltp__g_km_"}
              },
              "kw": {"avg": {"field": "ep__KW_"}},
              "mass": {"avg": {"field": "m__kg_"}},
              "cm3": {"avg": {"field": "ec__cm3_"}}
            }
          }

        },
        "size": 10
      }
    },
    "format": {"property": "aggregations.full_ewltp.buckets"}
  },
  "layer": [
    {
    selection: {

            grid: {type: "interval", bind: "scales"}
          }
      "mark": {"type": "point", "filled": true},
      "encoding": {
        "x": {
          "field": "kw.value",
          "type": "quantitative",
          "axis": {"title": "Avg engine power (kw)", "grid": false}
          "scale": {"zero": false}
        },
        "y": {
          "field": "ewltp.value",
          "type": "quantitative",
          "axis": {"title": "","grid": false}
          "scale": {"zero": false}

        },
        "tooltip": [
          {field: "key", title:"Country"}
          {field: "kw.value", title: "Engine power"}
          {field: "ewltp.value", type: "quantitative", title: "WLTP"}
        ] 
        "color":{value:"#58797F"}
      }
    }
    {
      "mark": {"type": "text", "filled": true, "dy": -5},
      "encoding": {
        "x": {
          "field": "kw.value",
          "type": "quantitative",
          "axis": {"grid": false}
          "scale": {"zero": false}

        },
        "y": {
          "field": "ewltp.value",
          "type": "quantitative",
          "axis": {"grid": false}
          "scale": {"zero": false}
        },
        "color":{value:"#58797F"}
        "tooltip": [
          {field: "key", title:"Country"}
          {field: "kw.value", title: "Engine power"}
          {field: "ewltp.value", type: "quantitative", title: "WLTP"}
        ] 
        "text": {"field": "key", "type": "nominal"}
      }
    }

  ],
  "size": 0
  config: {
    axis: {titleColor: "#69707D", titleFont: "Open Sans",labelColor: "#69707D", labelFont: "Open Sans", "titleFontSize":12}
  }
}"}}

